const getNestedValue = (obj: any, key: string): string | undefined => {
  return key.split('.').reduce((o, i) => (o ? o[i] : undefined), obj);
};

// FIX: Implement and export the useTranslations hook.
export const useTranslations = (initialLanguage: Language) => {
  const [language, setLanguage] = useState<Language>(initialLanguage);

  const t = useCallback(
    (key: string, replacements?: Record<string, string | number>): string => {
      const langPack = translations[language] || translations['zh-Hant'];
      let translation = getNestedValue(langPack, key);

      // Fallback to Chinese if a key is missing in the current language
      if (!translation && language !== 'zh-Hant') {
        const fallbackLangPack = translations['zh-Hant'];
        translation = getNestedValue(fallbackLangPack, key);
      }
      
      translation = translation || key; // Fallback to the key itself

      if (replacements) {
        Object.keys(replacements).forEach((placeholder) => {
          translation = (translation as string).replace(
            `{${placeholder}}`,
            String(replacements[placeholder])
          );
        });
      }
      return translation as string;
    },
    [language]
  );

  return { t, setLanguage };
};
